(function($){$.fn.showHide=function(options){var defaults={speed:500,easing:'',changeText:1,showText:'Show',hideText:'Hide'};var options=$.extend(defaults,options);$(this).click(function(){$('.toggleDiv').slideUp(options.speed,options.easing);var toggleClick=$(this);var toggleDiv=$(this).attr('rel');$(toggleDiv).slideToggle(options.speed,options.easing,function(){if(options.changeText==1){$(toggleDiv).is(":visible")?toggleClick.html(options.hideText):toggleClick.html(options.showText);}});return false;});};})(jQuery);
